function find_mean(A::AbstractVector, w::AbstractVector)
    @assert length(A) == length(w) "Lengths of vectors A and w must be equal."
    return dot(w, A) / sum(w)
end

# Example
A = [1.0, 2.0, 3.0, 4.0]
w = [0.1, 0.3, 0.4, 0.2]
println("The weighted mean is: ", find_mean(A, w))
